// client/src/App.js
import React, { useState } from 'react';
import { Container, Typography, TextField, Button, Box } from '@mui/material';
import FileUpload from './components/FileUpload';
import DownloadZipButton from './components/DownloadZipButton';

function App() {
  const [sessionId, setSessionId] = useState('');
  const [headers, setHeaders] = useState([]);
  const [prompt, setPrompt] = useState('');
  const [code, setCode] = useState('');
  const [output, setOutput] = useState(null);

  // When file is uploaded successfully
  const handleUploadSuccess = ({ sessionId, columns }) => {
    setSessionId(sessionId);
    setHeaders(columns);
    setCode('');
    setOutput(null);
    setPrompt('');
  };

  // Handle prompt submission
  const handleGenerateCode = async () => {
    if (!prompt || !headers.length || !sessionId) return;

    try {
      const response = await fetch('http://localhost:5000/generate-code', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt, headers, sessionId }),
      });

      const data = await response.json();

      if (data.code) {
        setCode(data.code);

        if (data.outputPreviewUrl) {
          setOutput({ type: 'text', url: `http://localhost:5000${data.outputPreviewUrl}` });
        } else if (data.plotUrl) {
          setOutput({ type: 'image', url: `http://localhost:5000${data.plotUrl}` });
        } else {
          setOutput(null);
        }
      } else {
        setCode('Failed to generate code.');
        setOutput(null);
      }
    } catch (err) {
      console.error('Error:', err);
      setCode('Server error. See console.');
      setOutput(null);
    }
  };

  return (
    <Container sx={{ mt: 5 }}>
      <Typography variant="h4" gutterBottom>ðŸ§  Prompt2LLM</Typography>

      <FileUpload onSuccess={handleUploadSuccess} />

      {sessionId && (
        <>
          <Box sx={{ mt: 3 }}>
            <TextField
              fullWidth
              label="Enter your ML prompt"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleGenerateCode()}
            />
            <Button sx={{ mt: 2 }} variant="contained" onClick={handleGenerateCode}>
              Generate Code
            </Button>
          </Box>

          {code && (
            <Box sx={{ mt: 4 }}>
              <Typography variant="h6">ðŸ”§ Generated Code:</Typography>
              <pre style={{ background: '#f5f5f5', padding: '1rem', overflowX: 'auto' }}>
                {code}
              </pre>
            </Box>
          )}

          {output && (
            <Box sx={{ mt: 2 }}>
              <Typography variant="h6">ðŸ“¤ Output:</Typography>
              {output.type === 'image' ? (
                <img src={output.url} alt="Output" style={{ maxWidth: '100%' }} />
              ) : (
                <iframe
                  src={output.url}
                  title="Text Output"
                  style={{ width: '100%', height: 200, border: '1px solid #ccc' }}
                />
              )}
            </Box>
          )}

          <DownloadZipButton sessionId={sessionId} />
        </>
      )}
    </Container>
  );
}

export default App;
