import React from 'react';

const DownloadZipButton = ({ sessionId }) => {
  const downloadZip = async () => {
    try {
      const res = await fetch(`http://localhost:5000/download/${sessionId}`);
      if (!res.ok) throw new Error('Failed to download ZIP');

      const blob = await res.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${sessionId}.zip`;
      a.click();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error('‚ùå Download failed:', err);
      alert('Download failed. Check if the session ZIP is ready.');
    }
  };

  return (
    <button
      
      className={`px-4 py-2 mt-4 rounded-2xl shadow-md font-semibold transition duration-200
        ${sessionId ? 'bg-blue-600 text-white hover:bg-blue-700' : 'bg-gray-300 text-gray-500 cursor-not-allowed'}`}
    >
    <Button
        variant="contained"
        color="primary"
        onClick={downloadZip}
        disabled={!sessionId}
        sx={{ mt: 2 }}
      ></Button>
      üì¶ Download ZIP
    </button>
  );
};

export default DownloadZipButton;
