const express = require('express');
const multer = require('multer');
const cors = require('cors');
const { spawn } = require('child_process');
const path = require('path');
const fs = require('fs'); // âœ… Make sure fs is imported

const app = express();
app.use(cors());
app.use(express.json());

const upload = multer({ dest: 'uploads/' });

// ===== UPLOAD ROUTE =====
app.post('/upload', upload.single('file'), (req, res) => {
  const file = req.file;
  const headers = ['age', 'gender', 'churn']; // Simulated for now
  res.json({ message: 'Upload successful', columns: headers });
});

// ===== GENERATE CODE ROUTE =====
app.post('/generate-code', (req, res) => {
  const { prompt, headers, sessionId } = req.body;

  if (!prompt || !headers || !sessionId) {
    return res.status(400).json({ error: 'Prompt, headers, and sessionId are required' });
  }

  const python = spawn('python', [
    path.join(__dirname, 'python', 'llm_engine.py'),
    prompt,
    JSON.stringify(headers),
    sessionId // âœ… Pass sessionId to Python for file naming
  ]);

  let output = '';
  let errorOutput = '';

  python.stdout.on('data', (data) => {
    output += data.toString();
  });

  python.stderr.on('data', (data) => {
    errorOutput += data.toString();
    console.error('ðŸ Python Error:', data.toString());
  });

  python.on('close', (code) => {
    if (code === 0) {
      res.json({ code: output });
    } else {
      res.status(500).json({
        error: 'Python script failed',
        details: errorOutput || 'Unknown error',
      });
    }
  });
});

// ===== DOWNLOAD ZIP ROUTE =====
app.get('/download/:sessionId', (req, res) => {
  const sessionId = req.params.sessionId;
  const zipPath = path.join(__dirname, 'sessions', `${sessionId}.zip`);

  if (fs.existsSync(zipPath)) {
    res.download(zipPath, `${sessionId}.zip`);
  } else {
    res.status(404).json({ error: 'ZIP file not found.' });
  }
});

app.listen(5000, () => {
  console.log('âœ… Server is running on port 5000');
});
