import React, { useState, useEffect } from 'react';
import FileUpload from './components/FileUpload';
import PromptInput from './components/PromptInput';
import { Container, Typography, Paper } from '@mui/material';
import axios from 'axios';

const App = () => {
  const [headers, setHeaders] = useState([]);
  const [generatedCode, setGeneratedCode] = useState('');
  const [sessionId, setSessionId] = useState(''); // âœ… Track session ID

  // âœ… Generate session ID once when component mounts
  useEffect(() => {
    const id = 'session_1234'; // You can change this to: `session_${Date.now()}`
    setSessionId(id);
  }, []);

  const handlePromptSubmit = async (prompt) => {
    try {
      const res = await axios.post('http://localhost:5000/generate-code', {
        prompt,
        headers,
        sessionId  // âœ… Include sessionId in the request
      });

      setGeneratedCode(res.data.code || 'No code returned.');
    } catch (err) {
      console.error('Error generating code:', err);
      setGeneratedCode('Error generating code.');
    }
  };

  return (
    <Container maxWidth="md">
      <Typography variant="h4" sx={{ mt: 4, mb: 2 }}>
        ðŸ§  Prompt2LLM â€“ ML Code Generator
      </Typography>
      <FileUpload onHeadersExtracted={setHeaders} />
      <PromptInput onSubmit={handlePromptSubmit} />
      {generatedCode && (
        <Paper elevation={3} sx={{ mt: 4, p: 3, whiteSpace: 'pre-wrap', fontFamily: 'monospace' }}>
          {generatedCode}
        </Paper>
      )}
    </Container>
  );
};

export default App;
