import React, { useState } from 'react';
import { Button, Box, Typography } from '@mui/material';
import axios from 'axios';

const FileUpload = ({ onHeadersExtracted }) => {
  const [file, setFile] = useState(null);
  const [response, setResponse] = useState('');

  const handleChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData);

      if (res.data && res.data.columns) {
        onHeadersExtracted(res.data.columns); // Send headers to App.js
        setResponse('File uploaded successfully!');
      } else {
        setResponse('Upload failed: No headers found.');
      }
    } catch (err) {
      console.error(err);
      setResponse('Upload failed.');
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h6">Upload CSV/XLS file</Typography>
      <input type="file" accept=".csv,.xlsx" onChange={handleChange} />
      <Button
        variant="contained"
        color="primary"
        onClick={handleUpload}
        sx={{ mt: 2 }}
      >
        Upload
      </Button>
      {response && <Typography sx={{ mt: 2 }}>{response}</Typography>}
    </Box>
  );
};

export default FileUpload;
