import React, { useState } from 'react';
import axios from 'axios';
import FileUpload from './components/FileUpload';
import PromptInput from './components/PromptInput';
import { Container, Typography, Divider, Box, Paper } from '@mui/material';

function App() {
  const [headers, setHeaders] = useState([]);
  const [userPrompt, setUserPrompt] = useState('');
  const [generatedCode, setGeneratedCode] = useState('');

  const handleFileUpload = (columns) => {
    setHeaders(columns); // from FileUpload component
  };

  const handlePromptSubmit = async (prompt) => {
    setUserPrompt(prompt);

    try {
      const res = await axios.post('http://localhost:5000/generate-code', {
        prompt,
        headers,
      });

      setGeneratedCode(res.data.code);
    } catch (err) {
      console.error('Error generating code:', err);
      setGeneratedCode('// Error generating code');
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom>
        Prompt2LLM
      </Typography>

      <FileUpload onHeadersExtracted={handleFileUpload} />
      <Divider sx={{ my: 4 }} />

      <PromptInput onSubmit={handlePromptSubmit} />

      {generatedCode && (
        <Box sx={{ mt: 4 }}>
          <Typography variant="h6">Generated ML Code:</Typography>
          <Paper sx={{ p: 2, mt: 1, backgroundColor: '#f5f5f5', whiteSpace: 'pre-wrap', fontFamily: 'monospace' }}>
            {generatedCode}
          </Paper>
        </Box>
      )}
    </Container>
  );
}

export default App;
