import React, { useState, useEffect } from 'react';
import FileUpload from './components/FileUpload';
import PromptInput from './components/PromptInput';
import DownloadZipButton from './components/DownloadZipButton';
import { Container, Typography, Paper } from '@mui/material';
import axios from 'axios';

const App = () => {
  const [headers, setHeaders] = useState([]);
  const [generatedCode, setGeneratedCode] = useState('');
  const [sessionId, setSessionId] = useState('');

  // âœ… Generate unique session ID once when component mounts
  useEffect(() => {
    const id = `session_${Date.now()}`;
    setSessionId(id);
  }, []);

  // âœ… Handle prompt submit and send sessionId to backend
  const handlePromptSubmit = async (prompt) => {
    try {
      const res = await axios.post('http://localhost:5000/generate-code', {
        prompt,
        headers,
        sessionId,
      });
      setGeneratedCode(res.data.code || 'No code returned.');
    } catch (err) {
      console.error('Error generating code:', err);
      setGeneratedCode('Error generating code.');
    }
  };

  return (
    <Container maxWidth="md">
      <Typography variant="h4" sx={{ mt: 4, mb: 2 }}>
        ðŸ§  Prompt2LLM â€“ ML Code Generator
      </Typography>

      <FileUpload onHeadersExtracted={setHeaders} />

      {/* âœ… Prompt Input */}
      <PromptInput onSubmit={handlePromptSubmit} />

      {/* âœ… Show code output + ZIP download */}
      {generatedCode && (
        <>
          <Paper
            elevation={3}
            sx={{
              mt: 4,
              p: 3,
              whiteSpace: 'pre-wrap',
              fontFamily: 'monospace',
              backgroundColor: '#f9f9f9',
            }}
          >
            {generatedCode}
          </Paper>
            
          <DownloadZipButton sessionId={sessionId} />
        </>
      )}
    </Container>
  );
};

export default App;
