// client/src/App.js
import React, { useState } from 'react';
import { Container, Typography, TextField, Button, Box } from '@mui/material';
import FileUpload from './components/FileUpload';
import DownloadZipButton from './components/DownloadZipButton';

function App() {
  const [sessionId, setSessionId] = useState('');
  const [headers, setHeaders] = useState([]);
  const [prompt, setPrompt] = useState('');
  const [code, setCode] = useState('');
  const [output, setOutput] = useState(null);
  const [step, setStep] = useState(null);

  const handleUploadSuccess = ({ sessionId, columns }) => {
    setSessionId(sessionId);
    setHeaders(columns);
    setCode('');
    setOutput(null);
    setPrompt('');
  };

  const handleGenerateCode = async () => {
    if (!prompt || !headers.length || !sessionId) return;

    try {
      const response = await fetch('http://localhost:5000/generate-code', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt, headers, sessionId }),
      });

      const data = await response.json();

      if (!response.ok) {
        console.error('‚ùå Backend Error:', data);
        setCode(`Error: ${data?.error || 'Server error'}`);
        return;
      }

      if (data.code) {
        setCode(data.code);
        setStep(data.step);

        // Try loading output (either text or image)
        const textUrl = `http://localhost:5000/sessions/${sessionId}/output_step_${data.step}.txt`;
        const imageUrl = `http://localhost:5000/sessions/${sessionId}/output_step_${data.step}.png`;

        const imageExists = await fetch(imageUrl).then(res => res.ok).catch(() => false);
        if (imageExists) {
          setOutput({ type: 'image', url: imageUrl });
          return;
        }

        const textExists = await fetch(textUrl);
        if (textExists.ok) {
          const textContent = await textExists.text();
          setOutput({ type: 'text', content: textContent });
        } else {
          setOutput(null);
        }
      } else {
        setCode('Failed to generate code.');
        setOutput(null);
      }
    } catch (err) {
      console.error('‚ùå Error during fetch:', err);
      setCode('Internal error. Check console.');
      setOutput(null);
    }
  };

  return (
    <Container sx={{ mt: 5 }}>
      <Typography variant="h4" gutterBottom>üß† Prompt2LLM</Typography>

      <FileUpload onSuccess={handleUploadSuccess} />

      {sessionId && (
        <>
          <Box sx={{ mt: 3 }}>
            <TextField
              fullWidth
              label="Enter your ML prompt"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleGenerateCode()}
            />
            <Button sx={{ mt: 2 }} variant="contained" onClick={handleGenerateCode}>
              Generate Code
            </Button>
          </Box>

          {code && (
            <Box sx={{ mt: 4 }}>
              <Typography variant="h6">üîß Generated Code:</Typography>
              <pre style={{ background: '#f5f5f5', padding: '1rem', overflowX: 'auto' }}>
                {code}
              </pre>
            </Box>
          )}

          {output && (
            <Box sx={{ mt: 2 }}>
              <Typography variant="h6">üì§ Output:</Typography>
              {output.type === 'image' ? (
                <img src={output.url} alt="Output" style={{ maxWidth: '100%' }} />
              ) : (
                <pre style={{ background: '#f0f0f0', padding: '1rem' }}>{output.content}</pre>
              )}
            </Box>
          )}

          <DownloadZipButton sessionId={sessionId} />
        </>
      )}
    </Container>
  );
}

export default App;
