import React, { useState } from 'react';
import PromptInput from './components/PromptInput';
import FileUpload from './components/FileUpload';
import Notebook from './components/Notebook';
import axios from 'axios';

function App() {
  const [headers, setHeaders] = useState([]);
  const [sessionId, setSessionId] = useState(null);
  const [steps, setSteps] = useState([]);

  const handleUploadSuccess = (columns, session) => {
    setHeaders(columns);
    setSessionId(session);
  };

  const handlePromptSubmit = async (prompt, sessionId) => {
    const res = await axios.post('http://localhost:5000/generate-code', {
      prompt,
      headers,
      sessionId,
    });

    const step = {
      prompt,
      code: res.data.code,
      step: res.data.step,
      sessionId,
      imageUrl: `step_${res.data.step}_output.png`, // try to show visual if exists
    };

    setSteps(prev => [...prev, step]);
  };

  return (
    <div style={{ padding: '20px' }}>
      <FileUpload
        onHeadersExtracted={(columns) => handleUploadSuccess(columns, 'session_1234')}
      />
      <PromptInput onSubmit={handlePromptSubmit} />
      <Notebook steps={steps} />
    </div>
  );
}

export default App;
